import style from './ShopPage.module.css';
import { useState } from 'react';

const products = [
    { 
        id: 1, 
        title: 'Latte', 
        price: 7.45,
        image: 'https://images.unsplash.com/photo-1561047029-3000c68339ca?w=300&h=300&fit=crop&crop=center'
    },
    { 
        id: 2, 
        title: 'Espresso', 
        price: 3.45,
        image: 'https://images.unsplash.com/photo-1510707577719-ae7c14805e3a?w=300&h=300&fit=crop&crop=center'
    },
    { 
        id: 3, 
        title: 'Macchiato', 
        price: 6.45,
        image: 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300&h=300&fit=crop&crop=center'
    },
    { 
        id: 4, 
        title: 'Cappucino', 
        price: 7.45,
        image: 'https://images.unsplash.com/photo-1572442388796-11668a67e53d?w=300&h=300&fit=crop&crop=center'
    },
];

const ShopPage = () => {
    const [cart, setCart] = useState([]);

    const addToCart = (product) => {
        setCart([...cart, product]);
    };

    const removeFromCart = (index) => {
        const newCart = cart.filter((_, i) => i !== index);
        setCart(newCart);
    };

    const total = cart.reduce((sum, item) => sum + item.price, 0);

    return (
        <div className={style.shop}>
            <div className={style.hero}>
                <h1 className={style.title}>‚òï Coffee Shop</h1>
                <p className={style.subtitle}>–°–≤–µ–∂–∏–π –∫–æ—Ñ–µ —Å —Å–æ–±–æ–π</p>
            </div>

            <section className={style.products}>
                <h2 className={style.sectionTitle}>–ù–∞—à–µ –º–µ–Ω—é</h2>
                <div className={style.productsGrid}>
                    {products.map((product) => (
                        <div key={product.id} className={style.productCard}>
                            <div className={style.productImage}>
                                <img src={product.image} alt={product.title} />
                            </div>
                            <div className={style.productInfo}>
                                <h3 className={style.productTitle}>{product.title}</h3>
                                <p className={style.productPrice}>${product.price}</p>
                                <button 
                                    className={style.addButton}
                                    onClick={() => addToCart(product)}
                                >
                                    –î–æ–±–∞–≤–∏—Ç—å –≤ –∫–æ—Ä–∑–∏–Ω—É
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </section>

            <section className={style.cart}>
                <h2 className={style.sectionTitle}>üõí –í–∞—à –∑–∞–∫–∞–∑</h2>
                {cart.length === 0 ? (
                    <div className={style.emptyCart}>
                        <p>–ö–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞</p>
                        <span className={style.emptyIcon}>üõí</span>
                    </div>
                ) : (
                    <div className={style.cartContent}>
                        <div className={style.cartItems}>
                            {cart.map((item, index) => (
                                <div key={index} className={style.cartItem}>
                                    <img src={item.image} alt={item.title} className={style.cartItemImage} />
                                    <div className={style.cartItemInfo}>
                                        <span className={style.cartItemTitle}>{item.title}</span>
                                        <span className={style.cartItemPrice}>${item.price}</span>
                                    </div>
                                    <button 
                                        className={style.removeButton}
                                        onClick={() => removeFromCart(index)}
                                    >
                                        √ó
                                    </button>
                                </div>
                            ))}
                        </div>
                        <div className={style.cartTotal}>
                            <strong>–ò—Ç–æ–≥–æ: ${total.toFixed(2)}</strong>
                            <button className={style.checkoutButton}>
                                –û—Ñ–æ—Ä–º–∏—Ç—å –∑–∞–∫–∞–∑
                            </button>
                        </div>
                    </div>
                )}
            </section>
        </div>
    );
};

export default ShopPage;
